<template>
  <div v-if="connectionStarted">
    <dx-data-grid
      id="gridContainer"
      :data-source="dataSource"
      :repaint-changes-only="true"
      :highlight-changes="true"
      :show-borders="true"
    >
      <dx-column
        :width="115"
        data-field="lastUpdate"
        data-type="date"
        format="longTime"
      />
      <dx-column
        data-field="symbol"
      />
      <dx-column
        data-field="price"
        data-type="number"
        format="#0.####"
        cell-template="priceCellTemplate"
      />
      <dx-column
        :width="140"
        data-field="change"
        data-type="number"
        format="#0.####"
        cell-template="changeCellTemplate"
      />
      <dx-column
        data-field="dayOpen"
        data-type="number"
        format="#0.####"
      />
      <dx-column
        data-field="dayMin"
        data-type="number"
        format="#0.####"
      />
      <dx-column
        data-field="dayMax"
        data-type="number"
        format="#0.####"
      />
      <div
        slot="priceCellTemplate"
        slot-scope="cellData"
      >
        <price-cell :cell-data="cellData"/>
      </div>
      <div
        slot="changeCellTemplate"
        slot-scope="cellData"
      >
        <change-cell :cell-data="cellData"/>
      </div>
    </dx-data-grid>
  </div>
</template>
<script>

import {
  DxDataGrid,
  DxColumn
} from 'devextreme-vue/data-grid';
import CustomStore from 'devextreme/data/custom_store';
import { HubConnectionBuilder } from '@aspnet/signalr';

import PriceCell from './PriceCell.vue';
import ChangeCell from './ChangeCell.vue';

export default {
  components: {
    DxDataGrid,
    DxColumn,
    PriceCell,
    ChangeCell
  },
  data() {
    return {
      connectionStarted: false,
      dataSource: null
    };
  },
  mounted() {
    var hubConnection = new HubConnectionBuilder()
      .withUrl('https://js.devexpress.com/Demos/NetCore/liveUpdateSignalRHub')
      .build();

    var store = new CustomStore({
      load: () => hubConnection.invoke('getAllStocks'),
      key: 'symbol'
    });

    hubConnection
      .start()
      .then(() => {
        hubConnection.on('updateStockPrice', (data) => {
          store.push([{ type: 'update', key: data.symbol, data: data }]);
        });
        this.dataSource = store;
        this.connectionStarted = true;
      });
  }
};
</script>

<style>
#gridContainer span.current-value {
    display: inline-block;
    margin-right: 5px;
}

#gridContainer span.diff {
    width: 50px;
    display: inline-block;
}

#gridContainer .inc {
    color: #2ab71b;
}

#gridContainer .dec {
    color: #f00;
}

#gridContainer .inc .arrow,
#gridContainer .dec .arrow {
    display: inline-block;
    height: 10px;
    width: 10px;
    background-repeat: no-repeat;
    background-size: 10px 10px;
}

#gridContainer .inc .arrow {
    background-image: url('data:image/png;base64,');
}

#gridContainer .dec .arrow {
    background-image: url('data:image/png;base64,');
}
</style>
